createContinuous(0, 250, {
  backgroundStyle: { fill: '#748de3', stroke: '#748de3' },
  text: { style: { fill: '#fff' } },
}).setIndicator(20);
createContinuous(0, 350, {
  text: {
    formatter: (val) => {
      return `${val}%`;
    },
  },
}).setIndicator(30);

