    轴线正方向: 'right',
    标题颜色: '#000',
    标题字号: 12,
    轴线颜色: '#000',
    轴线粗细: 2,
    刻度线颜色: '#000',
    刻度线粗细: 2,
    子刻度线颜色: '#000',
    子刻度线粗细: 2,
    子刻度线长度: 5,
    标签颜色: '#000',
    标签字号: 12
  };
  styleFolder.add(styleCfg, '末尾追加刻度').onChange(function (flag) {
    linear.update({
      tickLine: {
        appendTick: flag
      }
    });
  });
  styleFolder.add(styleCfg, '子刻度数量', 0, 9).step(1).onChange(function (count) {
    linear.update({
      subTickLine: {
        count: count
      }
    });
  });
  styleFolder.add(styleCfg, '轴线正方向', ['left', 'right']).onChange(function (dir) {
    linear.update({
      verticalFactor: dir === 'left' ? -1 : 1
    });
  });
  styleFolder.addColor(styleCfg, '标题颜色').onChange(function (fill) {
    linear.update({
      title: {
        style: {
          fill: fill
        }
      }
    });
  });
  styleFolder.add(styleCfg, '标题字号', 5, 20).onChange(function (fontSize) {
    linear.update({
      title: {
        style: {
          fontSize: fontSize
        }
      }
    });
  });
  styleFolder.addColor(styleCfg, '轴线颜色').onChange(function (color) {
    linear.update({
      axisLine: {
        style: {
          stroke: color
        }
      }
    });
  });
  styleFolder.add(styleCfg, '轴线粗细', 1, 5).step(1).onChange(function (lineWidth) {
    linear.update({
      axisLine: {
        style: {
          lineWidth: lineWidth
        }
      }
    });
  });
  styleFolder.addColor(styleCfg, '子刻度线颜色').onChange(function (stroke) {
    linear.update({
      subTickLine: {
        style: {
          stroke: stroke
        }
      }
    });
  });
  styleFolder.add(styleCfg, '子刻度线粗细', 1, 5).step(1).onChange(function (lineWidth) {
    linear.update({
      subTickLine: {
        style: {
          lineWidth: lineWidth
        }
      }
    });
  });
  styleFolder.add(styleCfg, '子刻度线长度', 0, 10).step(1).onChange(function (len) {
    linear.update({
      subTickLine: {
        len: len
      }
    });
  });
  styleFolder.addColor(styleCfg, '标签颜色').onChange(function (fill) {
    linear.update({
      label: {
        style: {
          default: {
            fill: fill
          }
        }
      }
    });
  });
